import './Card.scss';

export default function Card() {
  const handleClick = (side) => {
    const card = document.getElementById(side);
    console.log(card.classList);
    card.classList.toggle('card-front');
    card.classList.toggle('card-back');
  }
  return(
    <div className="card">
      <div className="card-inner">
        {/*Create the front of the card*/}
        <div className="card-front" id="front" onClick={() => handleClick('front')}>
          <img className="card-img" src="https://www.popsockets.com/dw/image/v2/BFSM_PRD/on/demandware.static/-/Sites-popsockets-master-catalog/default/dw9eb9511a/images/hi-res/Poke-Ball-Gloss_01_Top-View.png?sw=800&sh=800" />
        </div>

        {/*Create the back of the card*/}
        <div className="card-back" id="back" onClick={() => handleClick('back')}>

        </div>
      </div>
    </div>
  )
}
